import React from 'react';
import axios from 'axios';

class ShoppingCart extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      showCart: false
    };
    this.cartRef = React.createRef();
  }

  handleCartClick = async() => {
    if (!this.state.showCart) {
      document.addEventListener('mousedown', this.handleOutsideClick);
    } else {
      document.removeEventListener('mousedown', this.handleOutsideClick);
    }
    this.setState(prevState => ({
      showCart: !prevState.showCart
    }));
  }

  handleOutsideClick = (event) => {
    if (this.cartRef && !this.cartRef.current.contains(event.target)) {
      this.setState({ showCart: false });
      document.removeEventListener('mousedown', this.handleOutsideClick);
    }
  }

  render() {
    console.log(11111);
    console.log(this.props.items);
    return (
      <div>
        <button id='btnCart' style={styles.btnCart} onClick={this.handleCartClick}>購物車</button>
        {this.state.showCart && (
          <div ref={this.cartRef} style={styles.cartPanel}>
            <h2 style={styles.cartTitle}>購物清單</h2>
            <div style={styles.itemListContainer}>
              <ul style={styles.itemList}>
                {this.props.items.map((item, index) => {
                  console.log(222);
                  console.log(item);
                  return (
                    <li key={index} styles={styles.item}>
                      <div>
                        <div>
                          <img src="" alt="" />
                        </div>
                        <div>

                        </div>
                      </div>
                      <div>
                        <div>

                        </div>
                        <div>
                          <p></p>
                        </div>
                      </div>{item.productName} - {item.productFormat} - {item.cartQuantity} - {item.price}</li>
                  )
                })}
              </ul>
            </div>
            <button style={styles.checkoutButton}>結帳</button>
          </div>
        )}
      </div>
    );
  }
}

const styles = {
  btnCart: {
    position: 'fixed'
  },
  cartPanel: {
    position: 'absolute',
    top: '50px',
    // right: '10px',
    // width: '400px',
    padding: '10px',
    border: '1px solid #ccc',
    backgroundColor: '#fff',
    boxShadow: '0px 0px 10px rgba(0, 0, 0, 0.1)',
    display: 'flex',
    flexDirection: 'column',
    zIndex: '2',
    position: 'fixed',
    boxSizing: 'border-box'
  },
  cartTitle: {
    marginTop: 0,
    marginBottom: '10px',
  },
  itemListContainer: {
    width: '250px',
    flexGrow: 1,
    overflowY: 'auto', // 添加垂直滾動
    overflowX: 'hidden', // 隱藏水平滾動
    maxHeight: '300px', // 設置最大高度
    padding: "20px"

  },
  itemList: {
    listStyleType: 'none',
    padding: 0,
    margin: 0,
    width: '100%'
  },
  item: {
    padding: '5px 0',
    borderBottom: '1px solid #eee',
    textDecoration: 'none'
  },
  checkoutButton: {
    marginTop: '10px',
    padding: '10px',
    backgroundColor: '#4CAF50',
    color: 'white',
    border: 'none',
    cursor: 'pointer',
    width: '100%'
  }
};

export default ShoppingCart;
