import React, { Component, createRef } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Carousel } from 'bootstrap';

// import '../css/header.css';
// import '../css/footer.css';
// import '../js/header';
// import '../js/footer';
// import '../js/bootstrap.bundle'

import '../css/homepage.css';
import '../css/homepage_carousel.css';
// import '../js/homepage';

class ShopHomepage extends Component {

    state = {
        products: [
            {
                id: 1,
                name: '《wanwanO》無穀室內貓化毛調理配方｜貓顆粒乾糧',
                price: 300,
                salePrice: 240,
                imageUrl: [
                    'https://i.postimg.cc/6pwLs7LK/BOREAL.jpg',
                    'https://shoplineimg.com/63c12d63600b0a00b7c2ed0c/65d48acdf10879001aea9b25/800x.webp?source_format=png'],
                pSize: ['全齡貓(雞)', '熟齡貓(雞)'],
                pHint: '約180g'
            },
            {
                id: 2,
                name: '《wanwanO》無穀室內貓化毛調理配方｜貓顆粒乾糧《野起來吃 Wild Feeding》生食｜犬貓冷凍生食',
                price: 200,
                salePrice: 160,
                imageUrl: 'https://i.postimg.cc/X7BFTN6L/Wild-Feeding-300g.jpg',
                pSize: ['土雞', '鮭魚', '羊肉', '烏骨雞'],
                pHint: '約180g'
            },
            {
                id: 3,
                name: '《wanwan》注文時刻犬用營養餐包｜狗副食罐',
                price: 210,
                salePrice: 170,
                imageUrl: 'https://i.postimg.cc/2511gmJm/wanwan-80g-4.jpg',
                pSize: ['青汁嫩雞', '親子丼', '鮮雞4蔬'],
                pHint: '約180g'
            },
            {
                id: 4,
                name: '《wanwan》注文時刻犬用營養餐包｜狗副食罐',
                price: 210,
                salePrice: 170,
                imageUrl: 'https://i.postimg.cc/2511gmJm/wanwan-80g-4.jpg',
                pSize: ['青汁嫩雞', '親子丼', '鮮雞4蔬'],
                pHint: '約180g'
            },
            {
                id: 5,
                name: '《wanwan》注文時刻犬用營養餐包｜狗副食罐',
                price: 210,
                salePrice: 170,
                imageUrl: 'https://i.postimg.cc/2511gmJm/wanwan-80g-4.jpg',
                pSize: ['青汁嫩雞', '親子丼', '鮮雞4蔬'],
                pHint: '約180g'
            },
            {
                id: 6,
                name: '《wanwan》注文時刻犬用營養餐包｜狗副食罐',
                price: 210,
                salePrice: 170,
                imageUrl: 'https://i.postimg.cc/2511gmJm/wanwan-80g-4.jpg',
                pSize: ['青汁嫩雞', '親子丼', '鮮雞4蔬'],
                pHint: '約180g'
            },
            {
                id: 7,
                name: '《wanwan》注文時刻犬用營養餐包｜狗副食罐',
                price: 210,
                salePrice: 170,
                imageUrl: 'https://i.postimg.cc/2511gmJm/wanwan-80g-4.jpg',
                pSize: ['青汁嫩雞', '親子丼', '鮮雞4蔬'],
                pHint: '約180g'
            },
            {
                id: 8,
                name: '《wanwan》注文時刻犬用營養餐包｜狗副食罐',
                price: 210,
                salePrice: 170,
                imageUrl: 'https://i.postimg.cc/2511gmJm/wanwan-80g-4.jpg',
                pSize: ['青汁嫩雞', '親子丼', '鮮雞4蔬'],
                pHint: '約180g'
            }
        ],
        favoriteProducts: {},
        selectedSizes: {},
        currentImageIndex: {},
        quantities: {},
        isDown: false,
        startX: 0,
        scrollLeft: 0
    }


    // 輪播圖開始
    constructor(props) {
        super(props);
        this.carouselRef = React.createRef();
        this.carousel = null;
        this.containerRef = React.createRef();
    }

    componentDidMount = async () => {
        // 輪播
        if (this.carouselRef.current) {
            this.carousel = new Carousel(this.carouselRef.current, {
                interval: 3000,
                pause: 'hover'
            });
        }

        // 左右滑動功能
        const container = this.containerRef.current;
        container.addEventListener('mousedown', this.handleMouseDown);
        container.addEventListener('mouseleave', this.handleMouseLeave);
        container.addEventListener('mouseup', this.handleMouseUp);
        container.addEventListener('mousemove', this.handleMouseMove);

        
        // 連接資料庫
        var result = await axios.get("http://localhost:8000/shop");
        var newState = {...this.state};
        var pData = result.data.products.map((product, index) => {
            return (
                {
                    'id': product.productId,
                    'name': product.productName,
                    'price': product.price,
                    'salePrice': product.price * (product.Discount ? product.Discount : 1),
                    'imageUrl': product.productImgs,pSize: product.formats,
                    'pHint': '約180g'
                }
            )
        });
        newState.products = pData;
        this.setState(newState);
        
        const initialSelectedSizes = {};
        const initialImageIndices = {};
        this.state.products.forEach(product => {
            initialSelectedSizes[product.id] = product.pSize[0];  // 選擇第一個規格
            initialImageIndices[product.id] = 0;  // 選擇第一張圖片
        });
        this.setState({ 
            selectedSizes: initialSelectedSizes,
            currentImageIndex: initialImageIndices 
        });
    }

    componentWillUnmount() {
        if (this.carousel) {
            this.carousel.dispose();
        }

        // 左右滑動功能
        const container = this.containerRef.current;
        container.removeEventListener('mousedown', this.handleMouseDown);
        container.removeEventListener('mouseleave', this.handleMouseLeave);
        container.removeEventListener('mouseup', this.handleMouseUp);
        container.removeEventListener('mousemove', this.handleMouseMove);
    }

    handleMouseDown = (e) => {
        const container = this.containerRef.current;
        this.setState({
            isDown: true,
            startX: e.pageX - container.offsetLeft,
            scrollLeft: container.scrollLeft
        });
        container.classList.add('active');
    }

    handleMouseLeave = () => {
        this.setState({ isDown: false });
        this.containerRef.current.classList.remove('active');
    }

    handleMouseUp = () => {
        this.setState({ isDown: false });
        this.containerRef.current.classList.remove('active');
    }

    handleMouseMove = (e) => {
        if (!this.state.isDown) return;
        e.preventDefault();
        const container = this.containerRef.current;
        const x = e.pageX - container.offsetLeft;
        const walk = (x - this.state.startX) * 2; // scroll-fast
        container.scrollLeft = this.state.scrollLeft - walk;
    }

    // 輪播圖進入離開
    carouselMouseEnter = () => {
        if (this.carousel) {
            this.carousel.pause();
        }
    }

    carouselMouseLeave = () => {
        if (this.carousel) {
            this.carousel.cycle();
        }
    }

    // 收藏按鈕
    handleFavoriteClick = (productId) => {
        this.setState(prevState => ({
            favoriteProducts: {
                ...prevState.favoriteProducts,
                [productId]: !prevState.favoriteProducts[productId]
            }
        }));
    }

    // 規格按鈕


    handleSizeClick = (productId, size, index) => {
        this.setState(prevState => ({
            selectedSizes: {
                ...prevState.selectedSizes,
                [productId]: size
            },
            currentImageIndex: {
                ...prevState.currentImageIndex,
                [productId]: index % this.state.products.find(p => p.id === productId).imageUrl.length
            }
        }));
    }

    // 數量增減按鈕
    handleQuantityChange = (productId, change) => {
        this.setState(prevState => {
            const currentQuantity = prevState.quantities[productId] || 1;
            let newQuantity = currentQuantity + change;
            newQuantity = Math.max(1, newQuantity); // 確保數量不小於1

            return {
                quantities: {
                    ...prevState.quantities,
                    [productId]: newQuantity
                }
            };
        });
    }

    render() {

        return (
            <div>
                <h1>ShopHomepage</h1>
                {/* <!-- Header --> */}
                {/* <header className="homeNavBar">
                    <span className="navItemLeft" id="navHome">首頁</span>
                    <span className="navItemLeft" id="navReserve">預約服務</span>
                    <div className="logoBg">
                        <div>
                            <img id="navBarLogo" src="./image/dog.png" alt="Logo" />
                        </div>
                    </div>
                    <span className="navItemRight" id="navShop" href="./homepage.html">購物</span>
                    <span className="navItemRight" id="navEdu">衛教知識</span>
                    <div className="navIcon">
                        <a href="#"><i style={{color: 'black'}} className="fa-regular fa-user"></i></a>
                        <a href="#"><i style={{color: 'black'}} className="fa-solid fa-cart-shopping"></i></a>
                    </div>
                </header> */}
                {/* <!-- 底下是fontawesome icon  --> */}
                {/* <script src="https://kit.fontawesome.com/a89a722df2.js" crossOrigin="anonymous"></script> */}

                {/* <!-- 輪播圖 OK --> */}
                <div id="eventSlide" className="carousel slide" data-bs-ride="carousel" data-bs-interval="3000"
                    ref={this.carouselRef} onMouseEnter={this.carouselMouseEnter} onMouseLeave={this.carouselMouseLeave}>
                    <div className="carousel-inner">
                        <div className="carousel-item active">
                            <img className="" src="https://cdn-v4.petpetgo.com/1800/public/fc0bdf62-e954-43ec-9851-250bc0bac401.jpg"
                                alt="" />
                        </div>
                        <div className="carousel-item">
                            <img className="" src="https://cdn-v4.petpetgo.com/1800/public/401832d2-c93a-43b1-ae06-129eb124d2cf.jpg"
                                alt="" />
                        </div>
                        <div className="carousel-item">
                            <img className="" src="https://cdn-v4.petpetgo.com/1800/public/32267885-ccd8-422a-a671-748ea1447f13.jpg"
                                alt="" />
                        </div>
                    </div>
                </div>


                {/* <!-- 商品分類 OK --> */}
                <div id="productCategory">
                    <p className="titleName">商品類別</p>
                    <ul id="categories">
                        <li>
                            <a href="./category_page.html">
                                <img src="https://www.pet-pulse.com/petpulse_imgs/petpulse_1692935472" alt="" />
                            </a>
                            <a href="./category_page.html">
                                <p>飼料</p>
                                <img src="https://www.pet-pulse.com/petpulse_imgs/petpulse_1692935472" alt="" />
                            </a>
                        </li>
                        <li>
                            <a href="">
                                <img src="https://img.freepik.com/premium-photo/pet-care-concept-various-pet-accessories-yellow-background_154515-5788.jpg"
                                    alt="" />
                            </a>
                            <a href="#">
                                <p>玩具</p>
                                <img src="https://img.freepik.com/premium-photo/pet-care-concept-various-pet-accessories-yellow-background_154515-5788.jpg"
                                    alt="" />
                            </a>
                        </li>
                        <li>
                            <a href="">
                                <img src="https://cdn.thewirecutter.com/wp-content/media/2024/07/petcarriers-2048px-5235-3x2-1.jpg?auto=webp&quality=75&crop=4:3,smart&width=1024"
                                    alt="" />
                            </a>
                            <a href="#">
                                <p>寵物攜帶用品</p>
                                <img src="https://cdn.thewirecutter.com/wp-content/media/2024/07/petcarriers-2048px-5235-3x2-1.jpg?auto=webp&quality=75&crop=4:3,smart&width=1024"
                                    alt="" />
                            </a>
                        </li>
                        <li>
                            <a href="">
                                <img src="https://springerpets.com/cdn/shop/files/DSC02675_f4ad954c-281b-447e-9372-c778b931688f_1024x1024.jpg?v=1699474376"
                                    alt="" />
                            </a>
                            <a href="#">
                                <p>牽繩</p>
                                <img src="https://springerpets.com/cdn/shop/files/DSC02675_f4ad954c-281b-447e-9372-c778b931688f_1024x1024.jpg?v=1699474376"
                                    alt="" />
                            </a>
                        </li>
                    </ul>
                </div>

                {/* <!-- 熱銷商品卡片 OK --> */}
                {/* <div className="blockTitle titleName">
                    <p>熱銷商品</p>
                </div>
                <div className="bestsellerProduct">
                    {this.state.products.map(product => {
                        const isFavorited = this.state.favoriteProducts[product.id] || false;
                        const selectedSize = this.state.selectedSizes[product.id] || product.pSize[0];
                        const quantity = this.state.quantities[product.id] || 1;
                        return (
                            <div className="pCard">
                                <div className="pImage">
                                    <img src={product.imageUrl} alt="" />
                                    <button className={`btnFavorite ${isFavorited ? 'favorited' : ''}`}
                                        onClick={() => { this.handleFavoriteClick(product.id) }}>
                                        {isFavorited ? (
                                            <i
                                                className="fa fa-heart"
                                                style={{ fontSize: '1rem', color: 'red' }}
                                            ></i>
                                        ) : (
                                            <i className="fa fa-heart-o"></i>
                                        )}
                                    </button>
                                </div>
                                <p className="pName">{product.name}</p>
                                <p className="pPrice">{product.price}</p>
                                <p className="pSalePrice">{product.salePrice}</p>
