                                    <p className="pSizeName">規格</p>
                                    <div className="pSizes">
                                        {product.pSize.map((size, index) => {
                                            return (
                                                <div 
                                                    // className={`pSizeItem ${index == 0 ? "selected" : ""}`}
                                                    className={`pSizeItem ${selectedSize === size ? 'selected' : ''}`}
                                                    onClick={() => this.handleSizeClick(product.id, size)}>
                                                    {size}
                                                </div>
                                            )
                                        })}
                                    </div>
                                    <p className="pHint">{product.pHint}</p>
                                    <div className="pNumberCtrl">
                                        <div 
                                            className="cutNumber" 
                                            onClick={ () => { this.handleQuantityChange(product.id, -1) }}
                                        >
                                            <i className="bi bi-dash"></i>
                                        </div>
                                        <input 
                                            className="pNumber" type="number" name="pNumber" min="1" max="99" 
                                            value={quantity} readOnly />
                                        <div 
                                            className="plusNumber"
                                            onClick={ () => { this.handleQuantityChange(product.id, 1) }}
                                        >
                                            <i className="bi bi-plus"></i>
                                        </div>
                                        <button><i className="bi bi-cart-plus"></i></button>
                                    </div>
                                </div>
                            </div>
                        )
                    })}
                </div> */}

                {/* <!-- 品牌特價 --> */}
                <div className="brandSale">
                    <p className="titleName">品牌限時特價</p>
                    <div className="saleContainer" ref={this.containerRef}>
                        {this.state.products.map(product => {
                            const isFavorited = this.state.favoriteProducts[product.id] || false;
                            // const selectedSize = this.state.selectedSizes[product.id] || product.pSize[0];
                            const selectedSizes = this.state.selectedSizes;
                            const quantity = this.state.quantities[product.id] || 1;
                            const currentImageIndex = this.state.currentImageIndex;
                            return (
                                <div className="pCard">
                                    <div className="pImage">
                                        {/* <img src={product.imageUrl} alt="" /> */}
                                        <img src={product.imageUrl[currentImageIndex[product.id] || 0]} alt="" />
                                        <button className={`btnFavorite ${isFavorited ? 'favorited' : ''}`}
                                            onClick={() => { this.handleFavoriteClick(product.id) }}>
                                            {isFavorited ? (
                                                <i
                                                    className="fa fa-heart"
                                                    style={{ fontSize: '1rem', color: 'red' }}
                                                ></i>
                                            ) : (
                                                <i className="fa fa-heart-o"></i>
                                            )}
                                        </button>
                                    </div>
                                    <p className="pName">{product.name}</p>
                                    <p className="pPrice">{product.price}</p>
                                    <p className="pSalePrice">{product.salePrice}</p>
                                    <div className="pFastInfo">
                                        <p className="pSizeName">規格</p>
                                        <div className="pSizes">
                                            {product.pSize.map((size, index) => {
                                                return (
                                                    <div
                                                        // className={`pSizeItem ${index == 0 ? "selected" : ""}`}
                                                        // className={`pSizeItem ${selectedSize === size ? 'selected' : ''}`}
                                                        className={`pSizeItem ${selectedSizes[product.id] === size ? 'selected' : ''}`}
                                                        onClick={() => this.handleSizeClick(product.id, size, index)}>
                                                        {size}
                                                    </div>
                                                )
                                            })}
                                        </div>
                                        <p className="pHint">{product.pHint}</p>
                                        <div className="pNumberCtrl">
                                            <div
                                                className="cutNumber"
                                                onClick={() => { this.handleQuantityChange(product.id, -1) }}
                                            >
                                                <i className="bi bi-dash"></i>
                                            </div>
                                            <input
                                                className="pNumber" type="number" name="pNumber" min="1" max="99"
                                                value={quantity} readOnly />
                                            <div
                                                className="plusNumber"
                                                onClick={() => { this.handleQuantityChange(product.id, 1) }}
                                            >
                                                <i className="bi bi-plus"></i>
                                            </div>
                                            <button><i className="bi bi-cart-plus"></i></button>
                                        </div>
                                    </div>
                                </div>
                            )
