import React from 'react';

class ShoppingCart extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      showCart: false
    };
    this.cartRef = React.createRef();
  }

  handleCartClick = () => {
    if (!this.state.showCart) {
      document.addEventListener('mousedown', this.handleOutsideClick);
    } else {
      document.removeEventListener('mousedown', this.handleOutsideClick);
    }
    this.setState(prevState => ({
      showCart: !prevState.showCart
    }));
  }

  handleOutsideClick = (event) => {
    if (this.cartRef && !this.cartRef.current.contains(event.target)) {
      this.setState({ showCart: false });
      document.removeEventListener('mousedown', this.handleOutsideClick);
    }
  }

  render() {
    return (
      <div>
        <button id='btnCart' onClick={this.handleCartClick}>購物車</button>
        {this.state.showCart && (
          <div ref={this.cartRef} style={styles.cartPanel}>
            <h2>購物清單</h2>
            <ul style={styles.itemList}>
              {this.props.items.map((item, index) => (
                <li key={index}>{item.name} - ${item.price}</li>
              ))}
            </ul>
            <button style={styles.checkoutButton}>結帳</button>
          </div>
        )}
      </div>
    );
  }
}

const styles = {
  btnCart: {
    position: 'sticky'
  },
  cartPanel: {
    position: 'absolute',
    top: '50px',
    // right: '10px',
    width: '200px',
    padding: '10px',
    border: '1px solid #ccc',
    backgroundColor: '#fff',
    boxShadow: '0px 0px 10px rgba(0, 0, 0, 0.1)',
    display: 'flex',
    flexDirection: 'column',
    zIndex: '2'
  },
  itemList: {
    listStyleType: 'none',
    padding: 0,
    margin: 0,
    flexGrow: 1
  },
  checkoutButton: {
    marginTop: '10px',
    padding: '10px',
    backgroundColor: '#4CAF50',
    color: 'white',
    border: 'none',
    cursor: 'pointer',
    alignSelf: 'flex-end'
  }
};

export default ShoppingCart;
