import React, { Component, createRef } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap/dist/js/bootstrap.bundle.min.js";

class CarouselComponent extends Component {
  constructor(props) {
    super(props);
    this.carouselRef = createRef();
  }

  componentDidMount() {
    const carouselElement = this.carouselRef.current;
    if (carouselElement) {
      this.bootstrapCarousel = new window.bootstrap.Carousel(carouselElement, {
        interval: 3000,
        pause: false,
      });

      carouselElement.addEventListener("mouseenter", this.handleMouseEnter);
      carouselElement.addEventListener("mouseleave", this.handleMouseLeave);
    }
  }

  componentWillUnmount() {
    const carouselElement = this.carouselRef.current;
    if (carouselElement) {
      carouselElement.removeEventListener("mouseenter", this.handleMouseEnter);
      carouselElement.removeEventListener("mouseleave", this.handleMouseLeave);
      this.bootstrapCarousel.dispose();
    }
  }

  handleMouseEnter = () => {
    this.bootstrapCarousel.pause();
  };

  handleMouseLeave = () => {
    this.bootstrapCarousel.cycle();
  };

  render() {
    return (
      <div
        id="eventSlide"
        className="carousel slide"
        data-bs-ride="carousel"
        data-bs-interval="3000"
        ref={this.carouselRef}
      >
        <div className="carousel-inner">
          <div className="carousel-item active">
            <img
              className=""
              src="https://cdn-v4.petpetgo.com/1800/public/fc0bdf62-e954-43ec-9851-250bc0bac401.jpg"
              alt=""
            />
          </div>
          <div className="carousel-item">
            <img
              className=""
              src="https://cdn-v4.petpetgo.com/1800/public/401832d2-c93a-43b1-ae06-129eb124d2cf.jpg"
              alt=""
            />
          </div>
          <div className="carousel-item">
            <img
              className=""
              src="https://cdn-v4.petpetgo.com/1800/public/32267885-ccd8-422a-a671-748ea1447f13.jpg"
              alt=""
            />
          </div>
        </div>
      </div>
    );
  }
}

export default CarouselComponent;
